tables.table_schema:
CREATE TABLE recordings (
    recording_id SERIAL PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    description TEXT,
    date_recorded DATE
);

CREATE TABLE tracking_records (
    record_id SERIAL PRIMARY KEY,
    opportunity_id INT REFERENCES educational_opportunities(opportunity_id),
    recording_id INT REFERENCES recordings(recording_id),
    date_tracked DATE
);

CREATE TABLE registrations (
  registration_id SERIAL PRIMARY KEY,
  participant_id INT REFERENCES participants(participant_id),
  opportunity_id INT REFERENCES opportunities(opportunity_id),
  registration_date DATE
);

CREATE TABLE progress_tracking (
  tracking_id SERIAL PRIMARY KEY,
  registration_id INT REFERENCES registrations(registration_id),
  progress_status VARCHAR(50),
  progress_date DATE
);

CREATE TABLE Completion (
  completion_id UUID PRIMARY KEY,
  enrollment_id UUID REFERENCES Enrollment(enrollment_id),
  completion_date DATE,
  completion_status VARCHAR(20)
);
