tables.table_schema:
CREATE TABLE RightsIssues (
  issue_id INT PRIMARY KEY,
  issue_description TEXT,
  related_population INT,
  date_reported TIMESTAMP
);

CREATE TABLE rights (
    id INT PRIMARY KEY,
    right_name VARCHAR(50),
    description TEXT
);

CREATE TABLE protection (
    id INT PRIMARY KEY,
    protection_type VARCHAR(50),
    protection_level INT,
    protection_description TEXT
);

CREATE TABLE incidents (
    id INT PRIMARY KEY,
    incident_date DATE,
    incident_type VARCHAR(50),
    description TEXT
);

CREATE TABLE protection_measures (
	id SERIAL PRIMARY KEY,
	measure_name VARCHAR(100) NOT NULL,
	description TEXT,
	type VARCHAR(50)
);
