tables.table_schema:
CREATE TABLE donations (
 donation_id INT PRIMARY KEY,
 donor_name VARCHAR(255),
 donation_amount DECIMAL(10, 2),
 donation_date DATE
);

CREATE TABLE organizations (
 organization_id INT PRIMARY KEY,
 organization_name VARCHAR(255),
 mission_statement TEXT,
 total_donations_received DECIMAL(15, 2)
);

CREATE TABLE donations (
    donation_id SERIAL PRIMARY KEY,
    donor_name VARCHAR(100) NOT NULL,
    donation_amount DECIMAL NOT NULL,
    donation_date DATE NOT NULL
);

CREATE TABLE organizations (
    org_id SERIAL PRIMARY KEY,
    org_name VARCHAR(100) NOT NULL,
    org_email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE donors (
    donor_id SERIAL PRIMARY KEY,
    donor_email VARCHAR(100) UNIQUE NOT NULL,
    donor_address VARCHAR(255) NOT NULL
);

CREATE TABLE donation_log (
    log_id SERIAL PRIMARY KEY,
    donation_id INT REFERENCES donations(donation_id),
    donor_id INT REFERENCES donors(donor_id),
    donation_date TIMESTAMP NOT NULL
);

CREATE TABLE donors (
    donor_id UUID PRIMARY KEY,
    donor_name VARCHAR,
    email VARCHAR UNIQUE,
    donation_total FLOAT
);

CREATE TABLE organizations (
    organization_id UUID PRIMARY KEY,
    organization_name VARCHAR,
    location VARCHAR,
    total_donations FLOAT
);

CREATE TABLE categories (
    category_id UUID PRIMARY KEY,
    category_name VARCHAR,
    description TEXT
);
