tables.table_schema:
CREATE TABLE HumanRightsLegislation (
    country VARCHAR(255),
    legislation_name VARCHAR(255),
    legislation_year INT,
    policy_description TEXT
);

CREATE TABLE human_rights_legislation (
    id INT PRIMARY KEY,
    law_title VARCHAR(255),
    law_description TEXT,
    date_enacted DATE
);

CREATE TABLE policies (
    id INT PRIMARY KEY,
    policy_title VARCHAR(255),
    policy_description TEXT,
    date_implemented DATE
);

CREATE TABLE countries (
    id INT PRIMARY KEY,
    country_name VARCHAR(255),
    region VARCHAR(50),
    population INT
);

CREATE TABLE analysis (
    id INT PRIMARY KEY,
    analysis_topic VARCHAR(255),
    analysis_description TEXT,
    analyst_name VARCHAR(100)
);
