tables.table_schema:
CREATE TABLE HumanRightsIncidents (
  incident_id UUID PRIMARY KEY,
  incident_date DATE,
  country TEXT,
  incident_description TEXT
);

CREATE TABLE AidCoordination (
  coordination_id UUID PRIMARY KEY,
  incident_id UUID REFERENCES HumanRightsIncidents(incident_id),
  aid_organization TEXT,
  coordination_date DATE
);

CREATE TABLE aid_distribution (
    distribution_id INT PRIMARY KEY,
    organization_id INT,
    incident_id INT,
    distribution_date DATE
);
