tables.table_schema:
CREATE TABLE infrastructure_damage ( 
    id INT PRIMARY KEY, 
    location VARCHAR(255), 
    damage_type VARCHAR(255), 
    status VARCHAR(50) 
);

CREATE TABLE repair_reports ( 
    report_id INT PRIMARY KEY, 
    damage_id INT, 
    repair_description TEXT, 
    repair_cost DECIMAL 
);

CREATE TABLE decision_making ( 
    decision_id INT PRIMARY KEY, 
    report_id INT, 
    recommended_action TEXT, 
    decision_date DATE 
);

CREATE TABLE RepairTeams (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(255),
  team_lead VARCHAR(255),
  contact_number VARCHAR(20)
);

CREATE TABLE Inspections (
  inspection_id INT PRIMARY KEY,
  inspection_date DATE,
  inspector_name VARCHAR(255),
  findings TEXT
);

CREATE TABLE ReconstructionPlans (
  plan_id INT PRIMARY KEY,
  damage_report_id INT,
  repair_desc TEXT,
  timeline DATE
);

CREATE TABLE DecisionMaking (
  decision_id INT PRIMARY KEY,
  decision_date DATE,
  decision_maker VARCHAR(255),
  rationale TEXT
);
