tables.table_schema:
CREATE TABLE emergency_types (
  emergency_type_id SERIAL PRIMARY KEY,
  emergency_type_name VARCHAR(50) NOT NULL,
  emergency_description TEXT
);

CREATE TABLE response_times (
  report_id SERIAL PRIMARY KEY,
  emergency_type_id INT REFERENCES emergency_types(emergency_type_id),
  response_time_seconds INT NOT NULL,
  date_reported DATE
);

CREATE TABLE emergencies(
	emergency_id INT PRIMARY KEY,
	emergency_type VARCHAR(50),
	emergency_location VARCHAR(100),
	response_time_in_seconds INT
);

CREATE TABLE resources(
	resource_id INT PRIMARY KEY,
	resource_name VARCHAR(50),
	resource_type VARCHAR(50),
	availability BOOLEAN
);

CREATE TABLE response_times_emergency_types (
  id UUID PRIMARY KEY,
  emergency_type VARCHAR(50),
  response_time INTERVAL
);

CREATE TABLE resource_allocation (
  allocation_id INT PRIMARY KEY,
  report_id VARCHAR(10),
  resource_type VARCHAR(50),
  quantity INT
);

CREATE TABLE resource_inventory (
  resource_id INT PRIMARY KEY,
  resource_type VARCHAR(50),
  available_quantity INT
);

CREATE TABLE emergency_response_team (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(50),
  team_lead VARCHAR(100),
  team_size INT
);
