tables.table_schema:
CREATE TABLE emergency_logs (
   log_id INT PRIMARY KEY,
   responder_id INT,
   timestamp TIMESTAMP,
   log_message TEXT
);

CREATE TABLE responders (
   responder_id INT PRIMARY KEY,
   responder_name VARCHAR(50),
   role VARCHAR(50),
   contact_number VARCHAR(15)
);

CREATE TABLE emergency_responders (
    responder_id INT PRIMARY KEY,
    responder_name VARCHAR(50) NOT NULL,
    responder_role VARCHAR(50) NOT NULL
);

CREATE TABLE emergency_responders (
  responder_id INT PRIMARY KEY,
  responder_name VARCHAR(50) NOT NULL,
  responder_role VARCHAR(50) NOT NULL
);

CREATE TABLE crisis_situations (
  crisis_id INT PRIMARY KEY,
  crisis_location VARCHAR(100) NOT NULL,
  crisis_description TEXT NOT NULL,
  crisis_datetime DATETIME
);
