tables.table_schema:
CREATE TABLE emergency_contacts (
    contact_id INT PRIMARY KEY,
    contact_name VARCHAR(255),
    contact_phone VARCHAR(15),
    contact_email VARCHAR(255)
);

CREATE TABLE emergency_contacts (
  contact_id INT PRIMARY KEY,
  name VARCHAR(255),
  phone_number VARCHAR(20),
  relationship VARCHAR(100)
);

CREATE TABLE emergencies (
  emergency_id INT PRIMARY KEY,
  emergency_type VARCHAR(50),
  timestamp TIMESTAMP,
  location VARCHAR(255)
);

CREATE TABLE contacts (
    contact_id UUID PRIMARY KEY,
    contact_name VARCHAR(50) NOT NULL,
    contact_phone VARCHAR(15) NOT NULL,
    contact_email VARCHAR(50)
);

CREATE TABLE notifications (
    notification_id UUID PRIMARY KEY,
    notification_message TEXT NOT NULL,
    notification_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE contact_emergency (
    contact_id UUID REFERENCES contacts(contact_id),
    emergency_id UUID REFERENCES emergencies(emergency_id),
    PRIMARY KEY (contact_id, emergency_id),
    notification_id UUID REFERENCES notifications(notification_id)
);

CREATE TABLE emergency_contacts (
	contact_id UUID PRIMARY KEY,
	name STRING NOT NULL,
	email STRING NOT NULL,
	phone_number STRING NOT NULL
);

CREATE TABLE events (
	event_id UUID PRIMARY KEY,
	event_name STRING NOT NULL,
	event_date DATE NOT NULL,
	event_location STRING NOT NULL
);
