tables.table_schema:
CREATE TABLE Vaccination_Coverage (
    country_id INT NOT NULL,
    country_name VARCHAR(50) NOT NULL,
    coverage_rate DECIMAL(5,2) NOT NULL,
    last_update DATE NOT NULL
);

CREATE TABLE Immunization_Gaps (
    gap_id INT NOT NULL,
    gap_description VARCHAR(100) NOT NULL,
    country_id INT NOT NULL,
    gap_status BOOLEAN NOT NULL
);

CREATE TABLE VaccinationCoverage (
    id INT PRIMARY KEY,
    country_name VARCHAR(255) NOT NULL,
    coverage_rate DECIMAL(5,2) NOT NULL
);

CREATE TABLE ImmunizationPrograms (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(255) NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE NOT NULL
);

CREATE TABLE CoverageGaps (
    gap_id INT PRIMARY KEY,
    gap_description TEXT,
    country_name VARCHAR(255) NOT NULL
);

CREATE TABLE VaccinationCenters (
   center_id SERIAL PRIMARY KEY,
   center_name VARCHAR(100) NOT NULL,
   location VARCHAR(255) NOT NULL
);
