tables.table_schema:
CREATE TABLE pathogen_metadata ( 
    pathogen_id INT, 
    pathogen_name VARCHAR(100), 
    pathogen_type VARCHAR(50), 
    PRIMARY KEY (pathogen_id) 
);

CREATE TABLE drug_resistance_trends (
  pathogen_id INT NOT NULL,
  date_recorded DATE NOT NULL,
  drug_type VARCHAR(50) NOT NULL,
  drug_resistance_level INT NOT NULL
);

CREATE TABLE pathogen_info (
  pathogen_id INT NOT NULL PRIMARY KEY,
  pathogen_name VARCHAR(100) NOT NULL,
  species VARCHAR(100),
  origin_country VARCHAR(50) NOT NULL
);

CREATE TABLE Pathogens (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    type VARCHAR(50) NOT NULL,
    resistance_level VARCHAR(50) NOT NULL
);

CREATE TABLE Samples (
    sample_id SERIAL PRIMARY KEY,
    pathogen_id INT NOT NULL,
    collection_date DATE NOT NULL,
    sample_type VARCHAR(50) NOT NULL
);

CREATE TABLE Patients (
    patient_id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    age INT NOT NULL,
    gender VARCHAR(10) NOT NULL
);

CREATE TABLE pathogens (
  pathogen_id INT PRIMARY KEY,
  pathogen_name VARCHAR(255) NOT NULL,
  pathogen_species VARCHAR(255) NOT NULL
);

CREATE TABLE patients (
  patient_id INT PRIMARY KEY,
  patient_name VARCHAR(255) NOT NULL,
  age INT NOT NULL,
  gender VARCHAR(10) NOT NULL
);
