tables.table_schema:
CREATE TABLE patient_flow (
  patient_id INT PRIMARY KEY,
  patient_name VARCHAR(50),
  admission_date DATE,
  discharge_date DATE
);

CREATE TABLE bed_occupancy (
  bed_id INT PRIMARY KEY,
  bed_type VARCHAR(50),
  occupancy_status BOOLEAN
);

CREATE TABLE beds (
  bed_id INT PRIMARY KEY,
  bed_type VARCHAR,
  occupied BOOLEAN,
  facility_id INT
);

CREATE TABLE facilities (
  facility_id INT PRIMARY KEY,
  facility_name VARCHAR,
  location VARCHAR
);

CREATE TABLE bed_occupancy (
  occupancy_id INT PRIMARY KEY,
  bed_id INT,
  occupancy_status BOOLEAN,
  occupancy_timestamp TIMESTAMP
);
