tables.table_schema:
CREATE TABLE eligibility_criteria (
  program_id INT PRIMARY KEY,
  age_requirement INT,
  income_requirement DECIMAL
);

CREATE TABLE beneficiaries (
  beneficiary_id INT PRIMARY KEY,
  program_id INT,
  beneficiary_name VARCHAR(255),
  address VARCHAR(255)
);

CREATE TABLE eligibility_criteria (
  program_id INT PRIMARY KEY,
  minimum_age INT,
  required_income DECIMAL(10, 2),
  residency_status VARCHAR(50)
);

CREATE TABLE programs (
  program_id INT PRIMARY KEY,
  program_name VARCHAR(100),
  description TEXT
);

CREATE TABLE eligibility_criteria (
    program_id INT,
    age_criteria INT,
    income_criteria DECIMAL,
    has_dependents BOOLEAN
);

CREATE TABLE programs (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(50),
    description TEXT
);

CREATE TABLE program_beneficiaries (
    program_id INT,
    beneficiary_id INT
);

CREATE TABLE assistance_programs (
  program_id SERIAL PRIMARY KEY,
  program_name VARCHAR(100) NOT NULL,
  eligibility_criteria TEXT
);

CREATE TABLE beneficiaries (
  beneficiary_id SERIAL PRIMARY KEY,
  beneficiary_name VARCHAR(100) NOT NULL,
  age INT,
  address VARCHAR(255)
);

CREATE TABLE program_eligibility (
  program_id INT REFERENCES assistance_programs(program_id),
  income_range VARCHAR(50),
  eligibility_status BOOLEAN
);

CREATE TABLE beneficiary_programs (
  beneficiary_id INT REFERENCES beneficiaries(beneficiary_id),
  program_id INT REFERENCES assistance_programs(program_id),
  registration_date DATE
);

CREATE TABLE program_statistics (
  program_id INT REFERENCES assistance_programs(program_id),
  total_beneficiaries INT,
  success_rate FLOAT
);
