tables.table_schema:
CREATE TABLE MicrofinanceLoans (
    loan_id INT PRIMARY KEY,
    borrower_name VARCHAR(255),
    loan_amount DECIMAL,
    loan_status VARCHAR(50)
);

CREATE TABLE loan_details (
    loan_id INT PRIMARY KEY,
    entrepreneur_name VARCHAR(255),
    loan_amount DECIMAL(10, 2),
    loan_status VARCHAR(50)
);

CREATE TABLE repayment_schedule (
    repayment_id INT PRIMARY KEY,
    loan_id INT,
    repayment_amount DECIMAL(10, 2),
    payment_date DATE
);

CREATE TABLE borrower_details (
    borrower_id INT PRIMARY KEY,
    borrower_name VARCHAR(255),
    loan_id INT,
    contact_number VARCHAR(20)
);

CREATE TABLE LoanApplications (
    application_id SERIAL PRIMARY KEY,
    applicant_name VARCHAR(50) NOT NULL,
    loan_amount DECIMAL(10, 2) NOT NULL,
    application_date DATE
);

CREATE TABLE LoanStatus (
    status_id SERIAL PRIMARY KEY,
    application_id INT REFERENCES LoanApplications(application_id),
    application_status VARCHAR(20) NOT NULL,
    status_date DATE
);

CREATE TABLE LoanRepayments (
    repayment_id SERIAL PRIMARY KEY,
    application_id INT REFERENCES LoanApplications(application_id),
    repayment_amount DECIMAL(10, 2) NOT NULL,
    repayment_date DATE
);

CREATE TABLE LoanOfficers (
    officer_id SERIAL PRIMARY KEY,
    officer_name VARCHAR(50) NOT NULL
);

CREATE TABLE LoanReports (
    report_id SERIAL PRIMARY KEY,
    application_id INT REFERENCES LoanApplications(application_id),
    report_text TEXT NOT NULL,
    report_date DATE
);
