tables.table_schema:
CREATE TABLE FoodDistributor (
    distributor_id INT PRIMARY KEY,
    distributor_name STRING NOT NULL,
    region_id INT NOT NULL
);

CREATE TABLE FoodInventory (
    inventory_id INT PRIMARY KEY,
    food_item STRING NOT NULL,
    quantity INT NOT NULL,
    distributor_id INT REFERENCES FoodDistributor(distributor_id)
);

CREATE TABLE RegionalDistribution (
    distribution_id INT PRIMARY KEY,
    region_id INT NOT NULL,
    distributor_id INT REFERENCES FoodDistributor(distributor_id),
    inventory_id INT REFERENCES FoodInventory(inventory_id)
);

CREATE TABLE distribution_centers (
 id INT PRIMARY KEY,
 name VARCHAR(255) NOT NULL,
 location VARCHAR(255) NOT NULL,
 capacity INT
);

CREATE TABLE suppliers (
 id INT PRIMARY KEY,
 name VARCHAR(255) NOT NULL,
 location VARCHAR(255) NOT NULL,
 contact_person VARCHAR(255)
);

CREATE TABLE food_products (
 id INT PRIMARY KEY,
 name VARCHAR(255) NOT NULL,
 category VARCHAR(255) NOT NULL,
 price DECIMAL(8,2)
);
