tables.table_schema:
CREATE TABLE CommunicationLogs_relief_agencies (
  id INT PRIMARY KEY,
  agency_name VARCHAR(255),
  communication_date TIMESTAMP,
  message_text TEXT
);

CREATE TABLE CommunicationLogs_government_entities (
  id INT PRIMARY KEY,
  entity_name VARCHAR(255),
  communication_date TIMESTAMP,
  message_text TEXT
);

CREATE TABLE relief_agencies (
    agency_id INT PRIMARY KEY,
    agency_name VARCHAR(255),
    contact_person VARCHAR(255),
    contact_number VARCHAR(20)
);

CREATE TABLE government_entities (
    government_id INT PRIMARY KEY,
    government_name VARCHAR(255),
    government_type VARCHAR(50)
);

CREATE TABLE relief_agencies (
  id SERIAL PRIMARY KEY,
  agency_name VARCHAR(50) UNIQUE NOT NULL,
  contact_person VARCHAR(50) NOT NULL,
  phone_number VARCHAR(15) NOT NULL
);

CREATE TABLE government_entities (
  id SERIAL PRIMARY KEY,
  entity_name VARCHAR(50) UNIQUE NOT NULL,
  contact_person VARCHAR(50) NOT NULL,
  email VARCHAR(50) NOT NULL
);

CREATE TABLE affected_communities (
  id SERIAL PRIMARY KEY,
  community_name VARCHAR(50) NOT NULL,
  location VARCHAR(100) NOT NULL,
  population INT NOT NULL
);
