tables.table_schema:
CREATE TABLE conflict_resolution_activities (
    case_id INT PRIMARY KEY,
    case_name VARCHAR(255),
    progress_percentage INT
);

CREATE TABLE stakeholders (
    stakeholder_id INT PRIMARY KEY,
    stakeholder_name VARCHAR(255),
    role VARCHAR(50),
    contact_info VARCHAR(255)
);

CREATE TABLE conflict_resolution_cases (
  case_id UUID PRIMARY KEY,
  case_name TEXT NOT NULL,
  case_description TEXT,
  case_status TEXT
);

CREATE TABLE stakeholders (
  stakeholder_id UUID PRIMARY KEY,
  stakeholder_name TEXT NOT NULL,
  stakeholder_role TEXT,
  stakeholder_contact TEXT
);

CREATE TABLE activity_logs (
    log_id INT PRIMARY KEY,
    activity_id INT,
    log_description TEXT,
    log_date TIMESTAMP
);

CREATE TABLE progress_updates (
    update_id INT PRIMARY KEY,
    case_id INT,
    update_description TEXT,
    update_date TIMESTAMP
);
