tables.table_schema:
CREATE TABLE ConflictResolutionTimeline (
  id SERIAL PRIMARY KEY,
  dispute_id INT NOT NULL,
  resolution_deadline TIMESTAMP NOT NULL
);

CREATE TABLE ConflictResolutionTask (
  task_id SERIAL PRIMARY KEY,
  timeline_id INT NOT NULL,
  task_description TEXT,
  task_deadline TIMESTAMP NOT NULL
);

CREATE TABLE Deadlines (
    deadline_id INT PRIMARY KEY,
    deadline_name VARCHAR(100) NOT NULL,
    due_date DATE NOT NULL,
    resolution_status BOOLEAN
);

CREATE TABLE conflict_resolution_timeline (
 id INT PRIMARY KEY,
 event_name VARCHAR(255),
 start_date DATE,
 end_date DATE
);

CREATE TABLE dispute_deadlines (
 id INT PRIMARY KEY,
 case_id INT,
 deadline_date DATE,
 resolution_status VARCHAR(50)
);

CREATE TABLE resolution_history (
 id INT PRIMARY KEY,
 case_id INT,
 resolution_date DATE,
 result VARCHAR(100)
);

CREATE TABLE dispute_parties (
 party_id INT PRIMARY KEY,
 case_id INT,
 party_name VARCHAR(255),
 party_role VARCHAR(50)
);
