tables.table_schema:
CREATE TABLE conflicts_of_interest (
  mediator_id INT PRIMARY KEY,
  arbitrator_id INT,
  conflict_description VARCHAR(255),
  conflict_date DATE
);

CREATE TABLE conflict_flags (
  flag_id INT PRIMARY KEY,
  mediator_id INT,
  arbitrator_id INT,
  flagged_by_user VARCHAR(50),
  flag_reason TEXT
);

CREATE TABLE conflict_resolution_mediators (
  mediator_id INT PRIMARY KEY,
  mediator_name VARCHAR(50) NOT NULL,
  mediator_email VARCHAR(50) NOT NULL,
  conflict_resolution_type VARCHAR(20) NOT NULL
);

CREATE TABLE conflict_resolution_arbitrators (
  arbitrator_id INT PRIMARY KEY,
  arbitrator_name VARCHAR(50) NOT NULL,
  arbitrator_email VARCHAR(50) NOT NULL,
  conflict_resolution_type VARCHAR(20) NOT NULL
);

CREATE TABLE conflict_resolution_cases (
  case_id INT PRIMARY KEY,
  case_name VARCHAR(100) NOT NULL,
  case_description TEXT
);

CREATE TABLE conflict_resolution_flags (
  flag_id INT PRIMARY KEY,
  flag_type VARCHAR(30) NOT NULL,
  flagged_by VARCHAR(50) NOT NULL,
  flag_date DATE
);
