tables.table_schema:
CREATE TABLE emergency_contacts (
    contact_id UUID PRIMARY KEY,
    contact_name VARCHAR(255),
    contact_phone_number VARCHAR(20),
    contact_relation VARCHAR(50)
);

CREATE TABLE distress_areas (
    area_id UUID PRIMARY KEY,
    area_name VARCHAR(100),
    area_location GEOGRAPHY,
    area_population INT
);

CREATE TABLE distressed_areas (
  area_id INT PRIMARY KEY,
  area_name VARCHAR(50) NOT NULL,
  severity_level INT,
  contact_id INT REFERENCES emergency_contacts(contact_id)
);

CREATE TABLE assistance_logs (
  log_id INT PRIMARY KEY,
  individual_id INT REFERENCES individuals(individual_id),
  area_id INT REFERENCES distressed_areas(area_id),
  assistance_time TIMESTAMP
);

CREATE TABLE distressed_areas (
  area_id UUID PRIMARY KEY,
  area_name VARCHAR(100),
  location_point GEOMETRY(POINT)
);

CREATE TABLE individual_contacts (
  individual_id UUID PRIMARY KEY,
  contact_id UUID,
  area_id UUID,
  relationship VARCHAR(50)
);
