tables.table_schema:
CREATE TABLE water_quality_data ( 
   station_id INT, 
   date DATE, 
   pollutant_name VARCHAR(50), 
   pollutant_level FLOAT 
 );

CREATE TABLE monitoring_stations ( 
   station_id INT, 
   station_name VARCHAR(100), 
   location VARCHAR(255), 
   monitoring_date DATE 
 );

CREATE TABLE station_data (
    station_id UUID PRIMARY KEY,
    station_name STRING,
    location STRING,
    water_quality_reading FLOAT
);

CREATE TABLE monitoring_stations (
    station_id SERIAL PRIMARY KEY,
    station_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE pollution_sources (
    source_id SERIAL PRIMARY KEY,
    source_name VARCHAR(50) NOT NULL,
    source_type VARCHAR(50) NOT NULL
);

CREATE TABLE pollution_records (
    record_id SERIAL PRIMARY KEY,
    source_id INT REFERENCES pollution_sources(source_id),
    date DATE NOT NULL,
    description TEXT
);

CREATE TABLE Monitoring_Station (
    station_id INT PRIMARY KEY,
    station_name VARCHAR(255),
    location_lat FLOAT,
    location_long FLOAT
);

CREATE TABLE Pollution_Prevention (
    prevention_id INT PRIMARY KEY,
    source_id INT,
    prevention_method VARCHAR(255),
    prevention_description TEXT
);
