tables.table_schema:
CREATE TABLE greenhouse_emissions (
  id INT PRIMARY KEY,
  industry VARCHAR(255),
  region VARCHAR(255),
  emission_value FLOAT
);

CREATE TABLE emission_sources (
  id INT PRIMARY KEY,
  source_name VARCHAR(255),
  emission_category VARCHAR(255),
  emission_value FLOAT
);

CREATE TABLE industries (
	industry_id UUID PRIMARY KEY,
	industry_name TEXT,
	location TEXT
);

CREATE TABLE emissions (
	emission_id UUID PRIMARY KEY,
	industry_id UUID,
	emission_date DATE,
	emission_quantity FLOAT
);

CREATE TABLE regions (
	region_id UUID PRIMARY KEY,
	region_name TEXT,
	region_population INT
);

CREATE TABLE emissions_impact (
	emission_id UUID,
	region_id UUID,
	emission_impact TEXT
);

CREATE TABLE industry (
    industry_id INT PRIMARY KEY,
    industry_name VARCHAR(255) NOT NULL,
    industry_sector VARCHAR(255) NOT NULL
);

CREATE TABLE region (
    region_id INT PRIMARY KEY,
    region_name VARCHAR(255) NOT NULL,
    region_location VARCHAR(255) NOT NULL
);

CREATE TABLE climate_change_report (
    report_id INT PRIMARY KEY,
    report_date DATE,
    report_details TEXT
);
