tables.table_schema:
CREATE TABLE waste_disposal (
  id INT PRIMARY KEY,
  waste_type VARCHAR,
  disposal_date DATE,
  disposal_location VARCHAR
);

CREATE TABLE recycling_points (
  point_id INT PRIMARY KEY,
  location_name VARCHAR,
  coordinates GEOMETRY,
  disposal_capacity INT
);

CREATE TABLE recycled_materials (
  material_id INT PRIMARY KEY,
  material_type VARCHAR,
  recycling_point_id INT,
  collection_date DATE
);

CREATE TABLE WasteDisposalData (
  id INT PRIMARY KEY,
  waste_type VARCHAR(255) NOT NULL,
  weight FLOAT NOT NULL,
  collection_date DATE
);

CREATE TABLE RecyclingCenters (
  id INT PRIMARY KEY,
  location VARCHAR(255) NOT NULL,
  capacity FLOAT NOT NULL
);

CREATE TABLE RecyclingProcesses (
  id INT PRIMARY KEY,
  process_name VARCHAR(255) NOT NULL,
  efficiency FLOAT NOT NULL,
  start_date DATE
);

CREATE TABLE PollutionData (
  id INT PRIMARY KEY,
  location VARCHAR(255) NOT NULL,
  pollution_level FLOAT NOT NULL,
  measurement_date DATE
);
