tables.table_schema:
CREATE TABLE SoilQualityData (
  soil_id INT PRIMARY KEY,
  pH_level FLOAT,
  moisture_level FLOAT
);

CREATE TABLE FarmingPractices (
  practice_id INT PRIMARY KEY,
  practice_name STRING,
  optimal_season STRING,
  recommended_fertilizer STRING
);

CREATE TABLE FarmingPractices (
    practice_id INT PRIMARY KEY,
    practice_name VARCHAR,
    recommended_season VARCHAR,
    efficiency_rating INT
);

CREATE TABLE locations (
  location_id UUID PRIMARY KEY,
  region TEXT,
  soil_type TEXT,
  climate TEXT
);

CREATE TABLE farming_practices (
  practice_id UUID PRIMARY KEY,
  practice_name TEXT,
  description TEXT
);

CREATE TABLE analysis_results (
  analysis_id UUID PRIMARY KEY,
  sample_id UUID,
  practice_id UUID,
  optimal_condition BOOLEAN
);
