tables.table_schema:
CREATE TABLE wildlife_population (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(50),
    population_size INT,
    habitat_id INT
);

CREATE TABLE habitat (
    habitat_id INT PRIMARY KEY,
    habitat_name VARCHAR(50),
    habitat_type VARCHAR(50),
    area DECIMAL
);

CREATE TABLE WildlifePopulation (
	population_id INT PRIMARY KEY,
	species_name VARCHAR(50),
	population_size INT,
	habitat_type VARCHAR(50)
);

CREATE TABLE Habitat (
	habitat_id INT PRIMARY KEY,
	habitat_name VARCHAR(50),
	habitat_type VARCHAR(50),
	habitat_location VARCHAR(100)
);

CREATE TABLE Researcher (
	researcher_id INT PRIMARY KEY,
	researcher_name VARCHAR(50),
	researcher_email VARCHAR(50),
	researcher_area_of_expertise VARCHAR(50)
);
