tables.table_schema:
CREATE TABLE energy_consumption_data (
    building_id INT,
    date DATE,
    energy_consumed DECIMAL,
    PRIMARY KEY (building_id, date)
);

CREATE TABLE energy_efficiency (
    building_id INT,
    efficiency_rating INT,
    carbon_footprint DECIMAL,
    PRIMARY KEY (building_id)
);

CREATE TABLE buildings (
  building_id UUID PRIMARY KEY,
  building_name VARCHAR(255) NOT NULL,
  address VARCHAR(255) NOT NULL,
  total_energy_consumed DECIMAL(10,2) NOT NULL
);

CREATE TABLE energy_meters (
  meter_id UUID PRIMARY KEY,
  building_id UUID REFERENCES buildings(building_id),
  meter_type VARCHAR(50) NOT NULL,
  installation_date DATE NOT NULL
);

CREATE TABLE energy_consumption (
  consumption_id UUID PRIMARY KEY,
  meter_id UUID REFERENCES energy_meters(meter_id),
  consumption_date DATE NOT NULL,
  energy_consumed DECIMAL(10,2) NOT NULL
);

CREATE TABLE buildings (
    building_id INT PRIMARY KEY,
    building_name VARCHAR(255),
    address VARCHAR(255),
    built_year INT
);

CREATE TABLE energy_costs (
    cost_id INT PRIMARY KEY,
    reading_id INT,
    cost DECIMAL,
    CONSTRAINT fk_reading FOREIGN KEY (reading_id) REFERENCES energy_readings(reading_id)
);

CREATE TABLE energy_efficiency (
    building_id INT PRIMARY KEY,
    efficiency_rating DECIMAL,
    carbon_offset DECIMAL,
    CONSTRAINT fk_building FOREIGN KEY (building_id) REFERENCES buildings(building_id)
);

CREATE TABLE Building (
    building_id UUID PRIMARY KEY,
    building_name VARCHAR(255),
    address VARCHAR(255),
    area_sqft INT
);

CREATE TABLE EnergyConsumption (
    consumption_id UUID PRIMARY KEY,
    building_id UUID REFERENCES Building(building_id),
    date DATE,
    energy_kwh DECIMAL
);

CREATE TABLE EfficiencyImprovements (
    improvement_id UUID PRIMARY KEY,
    building_id UUID REFERENCES Building(building_id),
    improvement_type VARCHAR(255),
    cost DECIMAL
);

CREATE TABLE CarbonFootprint (
    footprint_id UUID PRIMARY KEY,
    building_id UUID REFERENCES Building(building_id),
    footprint_metric DECIMAL,
    date DATE
);

CREATE TABLE EnergySource (
    source_id UUID PRIMARY KEY,
    source_name VARCHAR(255),
    source_type VARCHAR(255),
    carbon_intensity DECIMAL
);
