tables.table_schema:
CREATE TABLE deforestation_data (
   date DATE NOT NULL,
   location VARCHAR NOT NULL,
   deforestation_rate DECIMAL NOT NULL
);

CREATE TABLE reforestation_data (
   date DATE NOT NULL,
   location VARCHAR NOT NULL,
   reforestation_efforts VARCHAR NOT NULL
);

CREATE TABLE forest_data (
    id INT PRIMARY KEY,
    region VARCHAR(50),
    deforestation_rate DECIMAL,
    reforestation_efforts BOOLEAN
);

CREATE TABLE tree_species (
    species_id INT PRIMARY KEY,
    species_name VARCHAR(50),
    growth_rate DECIMAL,
    average_height INT
);

CREATE TABLE ecosystem_health (
    ecosystem_id INT PRIMARY KEY,
    ecosystem_name VARCHAR(50),
    biodiversity_index DECIMAL,
    conservation_status VARCHAR(50)
);

CREATE TABLE Forests (
    forest_id INT,
    forest_name VARCHAR,
    location VARCHAR
);

CREATE TABLE DeforestationData (
    data_id INT,
    forest_id INT,
    deforestation_date DATE,
    deforestation_cause VARCHAR
);

CREATE TABLE ReforestationEfforts (
    reforestation_id INT,
    forest_id INT,
    reforestation_date DATE,
    reforestation_method VARCHAR
);

CREATE TABLE EcosystemPreservation (
    preservation_id INT,
    ecosystem_name VARCHAR,
    protected_area VARCHAR
);

CREATE TABLE LoggingActivities (
    logging_id INT,
    forest_id INT,
    logging_date DATE,
    logging_type VARCHAR
);
