CREATE TABLE women_shelters (
 id SERIAL PRIMARY KEY,
 name VARCHAR(100) NOT NULL,
 location VARCHAR(255) NOT NULL,
 capacity INT NOT NULL
);

CREATE TABLE donations (
   donation_id UUID PRIMARY KEY,
   donor_name VARCHAR(255) NOT NULL,
   donation_amount DECIMAL(10,2) NOT NULL,
   donation_date TIMESTAMP NOT NULL
);

CREATE TABLE shelters (
   shelter_id UUID PRIMARY KEY,
   shelter_name VARCHAR(255) NOT NULL,
   location VARCHAR(255) NOT NULL,
   capacity INT NOT NULL
);

CREATE TABLE distribution (
   distribution_id UUID PRIMARY KEY,
   donation_id UUID REFERENCES donations(donation_id),
   shelter_id UUID REFERENCES shelters(shelter_id),
   distribution_date TIMESTAMP NOT NULL
);

