tables.table_schema:
CREATE TABLE women_educational_achievements (
    // Table for storing women's educational achievements
    id INT PRIMARY KEY,
    name VARCHAR(50),
    level_of_education VARCHAR(50),
    achievement_description TEXT
);

CREATE TABLE scholarships_awarded (
    // Table for tracking scholarships awarded to women
    scholarship_id INT PRIMARY KEY,
    recipient_name VARCHAR(50),
    scholarship_amount DECIMAL(10, 2),
    sponsoring_organization VARCHAR(100)
);

CREATE TABLE women_education (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    education_level VARCHAR(50) NOT NULL,
    achievement VARCHAR(100) NOT NULL
);

CREATE TABLE gender_gap_progress (
    id INT PRIMARY KEY,
    year INT NOT NULL,
    gap_percentage DECIMAL(5, 2) NOT NULL
);

CREATE TABLE WomenEducationalAchievements (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    education_level VARCHAR(50),
    achievement VARCHAR(100)
);

CREATE TABLE Scholarships (
    id INT PRIMARY KEY,
    scholarship_name VARCHAR(100),
    recipient_id INT,
    amount DECIMAL(10, 2)
);

CREATE TABLE Recipients (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    gender VARCHAR(10),
    age INT
);

CREATE TABLE EducationalTracks (
    id INT PRIMARY KEY,
    track_name VARCHAR(50),
    description VARCHAR(200)
);

CREATE TABLE GenderEducationGap (
    year INT,
    gap_percentage DECIMAL(5, 2),
    PRIMARY KEY (year)
);
