tables.table_schema:
CREATE TABLE VirtualTeamMembers (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(50),
    member_email VARCHAR(100),
    member_position VARCHAR(50)
);

CREATE TABLE team_members (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(50),
    member_email VARCHAR(100),
    member_phone_number VARCHAR(20)
);

CREATE TABLE team_roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50),
    role_description VARCHAR(255)
);

CREATE TABLE team_members (
    member_id INT PRIMARY KEY,
    full_name VARCHAR(100),
    email VARCHAR(100),
    phone_number VARCHAR(15)
);

CREATE TABLE team_roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50)
);

CREATE TABLE Teams (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(255) NOT NULL
);

CREATE TABLE TeamAssignments (
    assignment_id INT PRIMARY KEY,
    team_id INT REFERENCES Teams(team_id),
    member_id INT REFERENCES TeamMembers(id),
    role VARCHAR(50) NOT NULL
);

CREATE TABLE TeamProjects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255) NOT NULL,
    team_id INT REFERENCES Teams(team_id),
    description TEXT
);

CREATE TABLE TeamMembers (
  member_id INT PRIMARY KEY,
  member_name VARCHAR(50),
  email VARCHAR(100),
  phone_number VARCHAR(15)
);

CREATE TABLE Teams (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(50),
  team_lead_id INT,
  FOREIGN KEY (team_lead_id) REFERENCES TeamMembers(member_id)
);

CREATE TABLE Projects (
  project_id INT PRIMARY KEY,
  project_name VARCHAR(100),
  team_id INT,
  FOREIGN KEY (team_id) REFERENCES Teams(team_id)
);

CREATE TABLE Skills (
  skill_id INT PRIMARY KEY,
  skill_name VARCHAR(50)
);
