tables.table_schema:
CREATE TABLE virtual_team (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(255),
    team_lead VARCHAR(255)
);
CREATE TABLE expenses (
    expense_id INT PRIMARY KEY,
    team_id INT,
    expense_description TEXT,
    amount DECIMAL(10, 2),
    expense_date DATE
);

CREATE TABLE virtual_team_members (
    member_id INT PRIMARY KEY,
    member_name TEXT NOT NULL,
    team_name TEXT NOT NULL
);

CREATE TABLE expenses (
    expense_id INT PRIMARY KEY,
    member_id INT REFERENCES virtual_team_members(member_id),
    expense_description TEXT,
    expense_amount DECIMAL
);

CREATE TABLE budget (
    budget_id INT PRIMARY KEY,
    team_name TEXT REFERENCES virtual_team_members(team_name),
    total_budget DECIMAL
);

CREATE TABLE team_members (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    email VARCHAR(50) UNIQUE NOT NULL
);

CREATE TABLE expenses (
    expense_id SERIAL PRIMARY KEY,
    date DATE,
    amount DECIMAL(10, 2) NOT NULL,
    description TEXT
);

CREATE TABLE budget (
    budget_id SERIAL PRIMARY KEY,
    year INT NOT NULL,
    total_budget DECIMAL(15, 2) NOT NULL
);

CREATE TABLE member_expenses (
    id INT,
    expense_id INT,
    FOREIGN KEY (id) REFERENCES team_members (id),
    FOREIGN KEY (expense_id) REFERENCES expenses (expense_id)
);

CREATE TABLE team_budget (
    team_id SERIAL PRIMARY KEY,
    year INT NOT NULL,
    total_assigned_budget DECIMAL(15, 2) NOT NULL
);
