tables.table_schema:
CREATE TABLE survey_data (
  id SERIAL PRIMARY KEY,
  question TEXT NOT NULL,
  response TEXT,
  respondent_id INT
);

CREATE TABLE respondent_info (
  respondent_id SERIAL PRIMARY KEY,
  name TEXT NOT NULL,
  age INT,
  email TEXT
);

CREATE TABLE remote_work_preferences (
    employee_id INT PRIMARY KEY,
    preferred_work_location VARCHAR,
    work_hours_per_day INT
);

CREATE TABLE remote_work_experiences (
    employee_id INT PRIMARY KEY,
    remote_work_duration INT,
    productivity_rating INT
);

CREATE TABLE remote_work_feedback (
    feedback_id INT PRIMARY KEY,
    employee_id INT,
    feedback_text TEXT
);

CREATE TABLE SurveyQuestions (
    question_id INT PRIMARY KEY,
    question_text TEXT,
    question_type VARCHAR(20)
);
