tables.table_schema:
CREATE TABLE employee_contacts (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    phone_number VARCHAR(15) NOT NULL
);

CREATE TABLE team_contacts (
    id INT PRIMARY KEY,
    team_name VARCHAR(50) NOT NULL,
    team_lead VARCHAR(50) NOT NULL,
    team_email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE EmployeeContacts (
    employee_id INT PRIMARY KEY,
    first_name VARCHAR(50),
    last_name VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE TeamContacts (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50),
    team_lead_id INT,
    FOREIGN KEY (team_lead_id) REFERENCES EmployeeContacts(employee_id)
);

CREATE TABLE Employees (
    employee_id INT PRIMARY KEY,
    full_name VARCHAR(100),
    email VARCHAR(100),
    phone_number VARCHAR(15)
);

CREATE TABLE Teams (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(100)
);

CREATE TABLE EmployeeTeams (
    employee_id INT,
    team_id INT,
    FOREIGN KEY(employee_id) REFERENCES Employees(employee_id),
    FOREIGN KEY(team_id) REFERENCES Teams(team_id)
);
