tables.table_schema:
CREATE TABLE RemoteEmployeeTrainingMaterials ( 
    employee_id INT PRIMARY KEY, 
    material_name VARCHAR(255), 
    material_type VARCHAR(50), 
    progress_percentage INT 
);

CREATE TABLE training_materials (
  id INT PRIMARY KEY,
  title VARCHAR(255),
  content TEXT,
  created_at TIMESTAMP
);

CREATE TABLE TrainingMaterials (
   material_id INT PRIMARY KEY,
   material_title VARCHAR(255),
   material_type VARCHAR(50),
   material_url VARCHAR(255)
);

CREATE TABLE EmployeeProgress (
   progress_id INT PRIMARY KEY,
   employee_id INT,
   material_id INT,
   progress_status VARCHAR(50)
);

CREATE TABLE Employees (
   employee_id INT PRIMARY KEY,
   employee_name VARCHAR(255),
   department VARCHAR(50),
   email VARCHAR(255)
);

CREATE TABLE Departments (
   department_id INT PRIMARY KEY,
   department_name VARCHAR(100),
   department_head VARCHAR(255)
);

CREATE TABLE departments (
    department_id INT PRIMARY KEY,
    department_name VARCHAR(255)
);

CREATE TABLE managers (
    manager_id INT PRIMARY KEY,
    manager_name VARCHAR(255),
    department_id INT
);
