tables.table_schema:
CREATE TABLE hot_desking (
    desk_id INT PRIMARY KEY,
    available BOOLEAN,
    last_used_date DATE
);

CREATE TABLE workspace_utilization (
  desk_id INT,
  utilization_date DATE,
  utilization_percentage FLOAT
);

CREATE TABLE Desk (
  desk_id INT PRIMARY KEY,
  location VARCHAR(255),
  capacity INT
);

CREATE TABLE Employee (
  employee_id INT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  department VARCHAR(100)
);

CREATE TABLE HotDesking (
  desk_id UUID PRIMARY KEY,
  is_available BOOLEAN NOT NULL,
  last_usage TIMESTAMP
);

CREATE TABLE Department (
  department_id UUID PRIMARY KEY,
  department_name VARCHAR(50) NOT NULL
);

CREATE TABLE Desk (
  desk_id UUID PRIMARY KEY,
  desk_name VARCHAR(50) NOT NULL,
  department_id UUID NOT NULL,
  is_hotdesk BOOLEAN NOT NULL
);
