tables.table_schema:
CREATE TABLE team_collaboration (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50),
    team_size INT,
    team_leader VARCHAR(50)
);

CREATE TABLE TeamMembers (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(255),
    role VARCHAR(50),
    department VARCHAR(50)
);

CREATE TABLE team_members( id INT PRIMARY KEY, name TEXT, role TEXT, department TEXT );
CREATE TABLE team_projects( id INT PRIMARY KEY, project_name TEXT, start_date DATE, end_date DATE, team_lead INT REFERENCES team_members(id) );
CREATE TABLE project_tasks( task_id INT PRIMARY KEY, task_name TEXT, status TEXT, assigned_to INT REFERENCES team_members(id), project_id INT REFERENCES team_projects(id) );
CREATE TABLE communication_log( log_id INT PRIMARY KEY, timestamp TIMESTAMP, sender_id INT REFERENCES team_members(id), receiver_id INT REFERENCES team_members(id), message TEXT );
CREATE TABLE collaboration_metrics( metric_id INT PRIMARY KEY, project_id INT REFERENCES team_projects(id), communication_score INT, collaboration_score INT );
