  employee_id INT NOT NULL,
  employee_name VARCHAR(50) NOT NULL,
  availability_time TIME,
  PRIMARY KEY (employee_id)
);

CREATE TABLE TeamMembers (
	employee_id INT PRIMARY KEY,
	employee_name VARCHAR(50) NOT NULL,
	employee_email VARCHAR(50) UNIQUE
);

