tables.table_schema:
CREATE TABLE remote_work_cafes (
  cafe_id INT PRIMARY KEY,
  cafe_name VARCHAR(50),
  location VARCHAR(100),
  wifi_speed INT
);

CREATE TABLE co_working_spaces (
  space_id INT PRIMARY KEY,
  space_name VARCHAR(50),
  location VARCHAR(100),
  amenities VARCHAR(200)
);

CREATE TABLE internet_speed_tests (
  test_id INT PRIMARY KEY,
  location VARCHAR(100),
  speed_download FLOAT,
  speed_upload FLOAT
);
