tables.table_schema:
CREATE TABLE employee_work_schedule (
    employee_id UUID PRIMARY KEY,
    work_schedule JSONB,
    availability JSONB,
    remote_meetings BOOLEAN
);

CREATE TABLE employee_work_schedules (
 employee_id INT,
 work_schedule VARCHAR(255),
 availability_for_meetings BOOLEAN
);

CREATE TABLE remote_meeting_availability (
 employee_id INT,
 meeting_date DATE,
 availability_status BOOLEAN
);

CREATE TABLE meeting_availability (
    meeting_id INT PRIMARY KEY,
    employee_id INT,
    available BOOLEAN,
    FOREIGN KEY (employee_id) REFERENCES employee_schedule(employee_id)
);

CREATE TABLE employee_availability (
    employee_id UUID PRIMARY KEY,
    availability_date DATE NOT NULL,
    available BOOLEAN
);

CREATE TABLE meeting_attendance (
    employee_id UUID,
    meeting_id UUID,
    is_attending BOOLEAN
);

CREATE TABLE meeting_notes (
    meeting_id UUID PRIMARY KEY,
    note_date DATE NOT NULL,
    note_text TEXT
);
