tables.table_schema:
CREATE TABLE RemoteWorkPolicyDocuments (
    policy_id INT PRIMARY KEY,
    document_title TEXT NOT NULL,
    document_content TEXT NOT NULL,
    last_updated DATE
);

CREATE TABLE remote_work_documents (
    document_id INT PRIMARY KEY,
    document_title VARCHAR(255),
    document_content TEXT,
    upload_date TIMESTAMP
);

CREATE TABLE employee_access (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(255),
    document_id INT,
    FOREIGN KEY (document_id) REFERENCES remote_work_documents(document_id)
);

CREATE TABLE remote_work_policies (
  policy_id INT PRIMARY KEY,
  policy_title VARCHAR(255),
  policy_content TEXT,
  created_at TIMESTAMP
);

CREATE TABLE employees (
  employee_id INT PRIMARY KEY,
  employee_name VARCHAR(255),
  department VARCHAR(255),
  is_manager BOOLEAN
);

CREATE TABLE access_logs (
  log_id INT PRIMARY KEY,
  employee_id INT,
  accessed_policy_id INT,
  access_time TIMESTAMP
);

CREATE TABLE departments (
  department_id INT PRIMARY KEY,
  department_name VARCHAR(255),
  manager_id INT
);
