tables.table_schema:
CREATE TABLE meeting_agendas (
    agenda_id UUID PRIMARY KEY,
    agenda_title VARCHAR(255) NOT NULL,
    start_time TIMESTAMP NOT NULL,
    end_time TIMESTAMP NOT NULL
);

CREATE TABLE meeting_notes (
    note_id UUID PRIMARY KEY,
    meeting_id UUID REFERENCES meeting_agendas(agenda_id) ON DELETE CASCADE,
    participant_id UUID REFERENCES participants(participant_id) ON DELETE CASCADE,
    note_text TEXT NOT NULL
);

CREATE TABLE schedules (
    schedule_id UUID PRIMARY KEY,
    meeting_id UUID REFERENCES meeting_agendas(agenda_id) ON DELETE CASCADE,
    participant_id UUID REFERENCES participants(participant_id) ON DELETE CASCADE,
    scheduled_time TIMESTAMP NOT NULL
);

CREATE TABLE MeetingAgenda (
    meeting_id INT PRIMARY KEY,
    agenda_item VARCHAR(255),
    description TEXT
);

CREATE TABLE Participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(100),
    email VARCHAR(255)
);

CREATE TABLE MeetingNotes (
    note_id INT PRIMARY KEY,
    meeting_id INT,
    participant_id INT,
    note TEXT
);

CREATE TABLE MeetingSchedule (
    schedule_id INT PRIMARY KEY,
    meeting_id INT,
    start_time TIMESTAMP,
    end_time TIMESTAMP
);

CREATE TABLE MeetingRoom (
    room_id INT PRIMARY KEY,
    room_name VARCHAR(100),
    capacity INT
);
