tables.table_schema:
CREATE TABLE SecurityIncidents (
  incident_id INT PRIMARY KEY,
  incident_date DATE,
  employee_id INT,
  incident_description TEXT
);

CREATE TABLE RemoteWorkLogs (
  log_id INT PRIMARY KEY,
  log_date DATE,
  employee_id INT,
  log_description TEXT
);

CREATE TABLE investigation_team (
  team_id INT NOT NULL,
  team_name VARCHAR(50),
  lead_investigator VARCHAR(100),
  case_notes TEXT
);

CREATE TABLE security_incidents ( 
 id INT PRIMARY KEY, 
 date TIMESTAMP, 
 incident_description TEXT, 
 severity INT 
);

CREATE TABLE employee_logs ( 
 id INT PRIMARY KEY, 
 employee_id INT, 
 log_description TEXT, 
 log_timestamp TIMESTAMP 
);

CREATE TABLE network_logs ( 
 id INT PRIMARY KEY, 
 network_device_id INT, 
 log_description TEXT, 
 log_timestamp TIMESTAMP 
);

CREATE TABLE forensic_reports ( 
 report_id INT PRIMARY KEY, 
 incident_id INT, 
 investigator_name TEXT, 
 report_details TEXT 
);

CREATE TABLE user_activities_logs ( 
 id INT PRIMARY KEY, 
 user_id INT, 
 activity_description TEXT, 
 activity_timestamp TIMESTAMP 
);
