tables.table_schema:
CREATE TABLE team_members (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(50),
    member_role VARCHAR(50)
);

CREATE TABLE team_members (
    member_id INT PRIMARY KEY,
    name TEXT,
    position TEXT,
    department TEXT
);

CREATE TABLE team_projects (
    project_id INT PRIMARY KEY,
    project_name TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE team_members (id INT PRIMARY KEY, name VARCHAR(255), email VARCHAR(255), role VARCHAR(50));
CREATE TABLE team_messages (message_id INT PRIMARY KEY, sender_id INT, receiver_id INT, message_text TEXT, timestamp TIMESTAMP);
CREATE TABLE team_channels (channel_id INT PRIMARY KEY, channel_name VARCHAR(100), topic TEXT);
CREATE TABLE team_files (file_id INT PRIMARY KEY, file_name VARCHAR(255), upload_date DATE, file_size INT);
CREATE TABLE team_meetings (meeting_id INT PRIMARY KEY, meeting_date DATE, duration TIME, agenda TEXT);
