tables.table_schema:
CREATE TABLE alerts (
    alert_id INT PRIMARY KEY,
    employee_id INT,
    concern VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE acknowledgements (
    ack_id INT PRIMARY KEY,
    alert_id INT,
    acknowledgement_status BOOLEAN,
    acknowledgement_timestamp TIMESTAMP
);

CREATE TABLE EmployeeConcerns (
  concern_id INT PRIMARY KEY,
  employee_id INT,
  concern_text TEXT,
  timestamp TIMESTAMP
);

CREATE TABLE Alerts (
  alert_id INT PRIMARY KEY,
  concern_id INT,
  alert_message TEXT,
  acknowledged BOOLEAN
);

CREATE TABLE EmployeeAcknowledgements (
  acknowledgement_id INT PRIMARY KEY,
  alert_id INT,
  employee_id INT,
  acknowledged_timestamp TIMESTAMP
);

CREATE TABLE Employee (
    employee_id INT PRIMARY KEY,
    employee_name VARCHAR(50),
    department VARCHAR(50),
    email VARCHAR(50)
);

CREATE TABLE Concern (
    concern_id INT PRIMARY KEY,
    employee_id INT,
    concern_text TEXT,
    submission_time TIMESTAMP
);

CREATE TABLE Acknowledgement (
    acknowledgement_id INT PRIMARY KEY,
    alert_id INT,
    employee_id INT,
    acknowledge_time TIMESTAMP
);
