tables.table_schema:
CREATE TABLE employee_training_materials (
    training_id SERIAL PRIMARY KEY,
    training_topic VARCHAR(100),
    training_description TEXT,
    training_material_url VARCHAR(255)
);

CREATE TABLE employee_progress_tracking (
    progress_id SERIAL PRIMARY KEY,
    employee_id INT,
    training_id INT,
    completion_status BOOLEAN,
    progress_date DATE
);

CREATE TABLE DiversityTrainingMaterials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(255) NOT NULL,
  material_description TEXT,
  material_link VARCHAR(255)
);

CREATE TABLE Employees (
  employee_id INT PRIMARY KEY,
  employee_name VARCHAR(255) NOT NULL,
  department VARCHAR(50)
);

CREATE TABLE Categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(50),
  category_description TEXT
);

CREATE TABLE training_materials (
  material_id INT PRIMARY KEY,
  material_name VARCHAR(255),
  material_description TEXT,
  upload_date DATE
);

CREATE TABLE material_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(100),
  category_description TEXT
);

CREATE TABLE employees (
  employee_id INT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  department VARCHAR(100)
);
