tables.table_schema:
CREATE TABLE leadership_roles (
    leader_id INT,
    group_name VARCHAR(255),
    leadership_title VARCHAR(255)
);

CREATE TABLE group_members (
    member_id INT,
    group_name VARCHAR(255),
    member_name VARCHAR(255),
    member_role VARCHAR(255)
);

CREATE TABLE LeadershipRoles (
    leader_id INT PRIMARY KEY,
    leader_name VARCHAR(255),
    group_id INT,
    leadership_position VARCHAR(255)
);

CREATE TABLE Groups (
    group_id INT PRIMARY KEY,
    group_name VARCHAR(255),
    group_type VARCHAR(255),
    group_description TEXT
);

CREATE TABLE Representation (
    representation_id INT PRIMARY KEY,
    group_id INT,
    leader_id INT,
    representation_type VARCHAR(255)
);
