tables.table_schema:
CREATE TABLE incidents (
    incident_id INT PRIMARY KEY,
    date_occurred DATE,
    location VARCHAR,
    description TEXT
);

CREATE TABLE investigators (
    investigator_id INT PRIMARY KEY,
    name VARCHAR,
    department VARCHAR
);

CREATE TABLE InvestigationTeam (
  team_id INT PRIMARY KEY,
  team_name VARCHAR(50),
  lead_investigator VARCHAR(50),
  location TEXT
);
