tables.table_schema:
CREATE TABLE MentorshipProgram (
    program_id INT PRIMARY KEY,
    program_name VARCHAR(255) NOT NULL,
    start_date DATE,
    end_date DATE
);

CREATE TABLE Mentors (
   mentor_id UUID PRIMARY KEY,
   mentor_name VARCHAR(50) NOT NULL,
   mentor_email VARCHAR(100) UNIQUE,
   mentor_area_of_expertise VARCHAR(100) NOT NULL
);

CREATE TABLE Mentees (
   mentee_id UUID PRIMARY KEY,
   mentee_name VARCHAR(50) NOT NULL,
   mentee_email VARCHAR(100) UNIQUE,
   mentee_department VARCHAR(100) NOT NULL
);

CREATE TABLE mentors (
    mentor_id INT PRIMARY KEY,
    mentor_name VARCHAR,
    gender VARCHAR,
    department VARCHAR
);

CREATE TABLE mentees (
    mentee_id INT PRIMARY KEY,
    mentee_name VARCHAR,
    gender VARCHAR,
    department VARCHAR
);

CREATE TABLE mentorship_feedback (
    feedback_id INT PRIMARY KEY,
    pair_id INT,
    feedback_text TEXT,
    rating INT
);
