tables.table_schema:
CREATE TABLE candidate_criteria (
    criteria_id INT PRIMARY KEY,
    criteria_name VARCHAR(255) NOT NULL,
    criteria_description TEXT
);
CREATE TABLE candidate_application (
    candidate_id INT PRIMARY KEY,
    candidate_name VARCHAR(255) NOT NULL,
    candidate_email VARCHAR(255) NOT NULL,
    criteria_id INT,
    FOREIGN KEY (criteria_id) REFERENCES candidate_criteria(criteria_id)
);

CREATE TABLE screening_criteria (
  id INT PRIMARY KEY,
  criteria_name VARCHAR(255),
  criteria_description TEXT,
  weight DECIMAL
);

CREATE TABLE ScreeningCriteria (
    criteria_id INT PRIMARY KEY,
    criteria_name VARCHAR(50) NOT NULL,
    criteria_description TEXT
);

CREATE TABLE CandidateFeedback (
    feedback_id INT PRIMARY KEY,
    candidate_id INT,
    feedback TEXT,
    feedback_rating INT
);

CREATE TABLE ScreeningResults (
    result_id INT PRIMARY KEY,
    candidate_id INT,
    criteria_id INT,
    result_value DECIMAL
);

CREATE TABLE ScreeningCriteria (
	criteria_id SERIAL PRIMARY KEY,
	criteria_description VARCHAR(255) NOT NULL,
	minimum_score INT NOT NULL
);

CREATE TABLE CandidateAssessments (
	assessment_id SERIAL PRIMARY KEY,
	candidate_id UUID REFERENCES Candidates(candidate_id),
	criteria_id INT REFERENCES ScreeningCriteria(criteria_id),
	score INT NOT NULL
);

CREATE TABLE ScreeningReports (
	report_id SERIAL PRIMARY KEY,
	candidate_id UUID REFERENCES Candidates(candidate_id),
	assessment_id INT REFERENCES CandidateAssessments(assessment_id),
	pass_status BOOLEAN
);
