tables.table_schema:
CREATE TABLE Employee( 
    employee_id INT PRIMARY KEY, 
    name VARCHAR(50), 
    department VARCHAR(50), 
    hire_date DATE 
);

CREATE TABLE Department( 
    department_id INT PRIMARY KEY, 
    name VARCHAR(50) 
);

CREATE TABLE Manager( 
    manager_id INT PRIMARY KEY, 
    employee_id INT 
);
